class Solution:
    def lengthOfLongestSubstring(self, s: str) -> int:
        n = len(s)
        m = 0
        if n<2:
            return n
        i = 0
        j = 0
        while i <= j < n:
            if s[j] not in s[i:j]:
                j += 1
            else:
                i += 1
            if m < (j-i):
                m = (j-i)
        return m
